  function createSkinBlock(colors, callback) {
    var $block = $('<div />', {
      'class': 'd-flex flex-wrap mb-3'
    })

    colors.map(function (color) {
      var $color = $('<div />', {
        'class': (typeof color === 'object' ? color.join(' ') : color).replace('navbar-', 'bg-').replace('accent-', 'bg-') + ' elevation-2'
      })

      $block.append($color)

      $color.data('color', color)

      $color.css({
        width       : '40px',
        height      : '20px',
        borderRadius: '25px',
        marginRight : 10,
        marginBottom: 10,
        opacity     : 0.8,
        cursor      : 'pointer'
      })

      $color.hover(function () {
        $(this).css({ opacity: 1 }).removeClass('elevation-2').addClass('elevation-4')
      }, function () {
        $(this).css({ opacity: 0.8 }).removeClass('elevation-4').addClass('elevation-2')
      })

      if (callback) {
        $color.on('click', callback)
      }
    })

    return $block
  }
  $.fn.extend({
	 renderData: function(template, obj) {
		 $(this).html("");
		 if (obj instanceof Array) {
			 for(var i = 0; i < obj.length; i++) {
				obj[i]["index"] = i + 1;
				$(this).append($(dataRender(template, obj[i])).removeClass("display-none"));
			}
		 } else {
			$(this).html($(dataRender(template, obj)));
		}
	 },
	 loadSelect: function(url,value,text,caller) {
		 var $this = this;
    	 $.get(url,function(data,status) {
    		 var select = $($this);
    		 select.html("");
             for (var i = 0; i < data.length; i++) { 
             	select.append("<option value='"+data[i][value]+"'>" + data[i][text] + "</option>");
             }
             caller(data);
    	 })
    }
  })
  
  function dataRender(templet,data) {
    var s = templet.match(/\$\{(\S*)\}/g);
    for(var i = 0; i < s.length; i++) {
        var key = s[i].match(/{(\S*)}/)[1];
        key = key.replace(/^\s+/, '').replace(/\s+$/, '');
        templet = templet.replace(s[i],data[key]);
    }
    return templet;
  }
