function select_copy (e, event){
	$(e).select();
	document.execCommand("Copy");
	var div = document.createElement('div')
	$(div).addClass('log_part')
	$(div).html('<p>内容已复制</p>')
	$(document.body).append(div)
	setTimeout(function(){
		$(div).remove()
	}, 1000)
//	var showbox = showMessage(e,"已复制", event);
//	setTimeout(function(){
//		$(showbox).remove();
//	}, 1000)
}

function showMessage(thisObj, message, event) {
	var posX = 0, posY = 0;
	var event = event || window.event;
	if (event.pageX || event.pageY) {
	    posX = event.pageX - document.documentElement.scrollLeft;;
	    posY = event.pageY - document.documentElement.scrollTop;;
	} else if (event.clientX || event.clientY) {
	    posX = event.clientX - document.documentElement.scrollLeft - document.body.scrollLeft;
	    posY = event.clientY - document.documentElement.scrollTop - document.body.scrollTop;
	}
    var showbox=$('<div>'+message+'</div>').css({
                  padding:'2px 5px',
                  position:'fixed',
                  zIndex: 9999,
                  fontSize: "12px",
                  display:"block",
                  lineHeight:"30px",
                  top:posY + 3,
                  left:posX + 3,
                  border:'1px solid #CCC',
                  borderRadius:'2px',
                  background: '#fff',
                }).addClass("showbox");
    showbox.insertAfter(thisObj);
    return showbox;
}

function hideMessage() {
	$(".showbox").remove();
}

loadModal();
function loadModal() {
	var body = $('body');
	var modalDiv = $("<div></div>").appendTo(body);
	$.ajaxSettings.async = false;
	$.get("../../common/modal.html",function(value,status){
		modalDiv.html(value);
	})
	$.ajaxSettings.async = true;
}
function getRole() {
	var role = sessionStorage.getItem('guide_role');
	if (!role) {
		$.ajaxSettings.async = false;
		$.get("getRole",function(value,status){
			if (value) {
				role = value;
				sessionStorage.setItem("guide_role", role);
			}
		})
		$.ajaxSettings.async = true;
	}
	return role;
}